tinymce.PluginManager.add("videox", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入Bilibili",
            body: {
                type: "panel",
                items: [
                    {
                        type: "input",
                        initValue: "请输入BV号",
                        name: "videox",
                        label: `请填入BV号或链接地址。<a style="color: #0366D6;" href="/tool/686/" target="_blank">[点击查看帮助]</a>`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();
                let videox = data.videox;
                if (videox.startsWith("https://www.bilibili.com/video/")) {
                    const regex = /https:\/\/www.bilibili.com\/video\/(BV\w+)/;
                    const found = videox.match(regex);
                    if (found) {
                        videox = found[1];
                    }
                }
                if (!videox) {
                    alert("请输入BV号");
                    return;
                }
                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`<iframe class="w-player-bilibili" src="https://player.bilibili.com/player.html?bvid=${videox}" scrolling="no" border="0" frameborder="no" framespacing="0" allowfullscreen="true"> </iframe>`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("videox", {
        icon : 'videox',
        tooltip: "插入视频",
        // text: '插入视频',
        onAction: function () {
            openDialog();
        },
    });

    // 注册一个菜单项名称 menu/menubar
    // editor.ui.registry.addMenuItem("videox", {
    //     text: "Example菜单名",
    //     onAction: function () {
    //         openDialog();
    //     },
    // });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "videox", //插件名称
                url: "https://github.com/iRuxu", //作者网址
            };
        },
    };
});
